import argparse
import sys

from pip._vendor.certifi import contents, where

def main():
    parser = argparse.ArgumentParser(description="Display certifi contents or location.")
    parser.add_argument("-c", "--contents", action="store_true", help="Display the contents of certifi.")
    args = parser.parse_args()

    if args.contents:
        print(contents())
    else:
        print(where())

if __name__ == "__main__":
    try:
        main()
    except Exception as e:
        print(f"An error occurred: {e}", file=sys.stderr)
        sys.exit(1)
